// minimum window substring 
// input is two strings (s and t)
// of length m and n respectively 
// goal is to find the MINIMUM substring within s that contains all characters of t, including duplicates 


// "ADOBECODEBANC" "ABC"
// "BANC" because it is the substrings with the least characters that still contain all the characters of ABC 
// start with pointers
// both on the left 
// same character 
// check if that substring as all the characters, if not then move the second pointer to the right by one 
// repeat the check 
// if at any point you reach a substring with all the characters of the target, close the first pointer 
// check if the closed substring has all the characters, if not then increase the second pointer again 
// repeat until both pointers reach the last character 

// to check if a substring contains every character, a helper function maybe helpful 
// pass in the substring, and the target 
// would need to check that the substring contains not just the same characters but also in the same frequency 

// at any point if the substring contains all the target characters, record that length 
// if you find a substring with a smaller length, record that new length 

// also have to account for capital and non capital letters? 

function func(s, t) {

    // records the smallest substring, 
    let smallest = ""; 
    let smallestCount = Infinity; 
    let left = 0; 
    let right = 0; 

    while(left <= s.length && right <= s.length) {
        let currStr = s.slice(left, right + 1); // get a copy of the current string 
        let match = checkMatch(currStr, t); 
        if(match) {
            if(currStr.length < smallestCount) {
                smallest = currStr; 
                smallestCount = currStr.length;  
            } 
            left++; 
        } else {
            right++; 
        }
    }

    return smallest;  
}

function checkMatch(s, t) {
    let tCount = {}; 
    let sCount = {}; 

    for(let char of s) {
        if(!sCount[char]) {
            sCount[char] = 1; 
        } else {
            sCount[char]++; 
        }
    }

    for(let char of t) {
        if(!tCount[char]) {
            tCount[char] = 1; 
        } else {
            tCount[char]++; 
        }
    }

    for(let char of t) {
        if(!sCount[char] || sCount[char] < tCount[char]) return false; 
    }
    return true; 
}

