// too slow 
// the window approach works but the I believe the comparison is too slow 
// count the target characters 

function optFunc(s, t) {

    let shortest = ''; 

    // create a hash map for the the target characters, which serves as a counter for how many of the characters you have found  while
    // iterating through the string 
    let tCount = {}; 
    for(let char of t) {
        if(!tCount[char]) {
            tCount[char] = 1; 
        } else {
            tCount[char]++; 
        }
    }

    //create a variable to signify when all characters from the target has been found
    //only get's decremented when all frequency of a character in tCount is found (the value of each char reaches below 0)  
    let charFound = Object.keys(tCount).length; 
    console.log(charFound);

    let left = 0; 
    let right = -1; // why does this need to be -1 

    // if the right pointer goes past the last char, it must mean that you've traversed the entire string and moving it further is impossible 
    while(right < s.length) {
        // two main conditions, 
        if(charFound === 0) {
            // this condition is if the current substring CONTAINS all the target characters
            // the goal here is then to minimize the window and see if you can find the smallest substring 
            // obviously an important condition if if the substring is shorter than the existing substring 

            if(!shortest || shortest.length > right - left + 1) {
                shortest = s.slice(left, right + 1)
            }

            if(tCount[s[left]] !== undefined) {
                tCount[s[left]]++; 
            }

            if(tCount[s[left]] > 0) {
                charFound++; 
            } 

            left++; 
            
        } else {
            // this condition is if the current substring does not contain all the target characters 
            // you want to add characters to it 
            // move the right pointer
            right++; 
            // decrement the frequency of the char in tcount if you found it by expanding the right pointer
            // you need the undefined here because 
            if(tCount[s[right]] !== undefined) {
                tCount[s[right]]--;
            }

            // if you've found all frequency of a target char, decrement count
            if(tCount[s[right]] === 0) {
                charFound--; 
            }
        }
        
    }

    return shortest;

}

